import React from "react";

export default function WeatherIcon(props) {
  const brokenCloudsDay = "broken-clouds-day";
  const codeMapping = {
    "01d": "clear-day",
    "01n": "clear-nigt",
    "02d": "scattered-clouds",
    "02n": "scattered-clouds",
    "03d": brokenCloudsDay,
    "03n": "broken-clouds-night",
    "04d": brokenCloudsDay,
    "04n": "broken-clouds-night",
    "09d": "LIGHT_RAIN",
    "09n": "MOON_LIGHT_RAIN",
    "10d": "HEAVY_RAIN",
    "10n": "MOON_HEAVY_RAIN",
    "11d": "STORM",
    "11n": "MOON_STORM",
    "13d": "SNOW",
    "13n": "MOON_SNOW",
  };

  return (
    <div>
      <img src={`images/${codeMapping[props.code]}.svg`} alt={props.alt} />
    </div>
  );
}
