import React, { useState } from "react";
import axios from "axios";

export default function Search() {
  const [city, setCity] = useState(null);
  const [description, setDescription] = useState(null);
  const [icon, setIcon] = useState(null);
  const [temperature, setTemperature] = useState(null);
  const [humidity, setHumidity] = useState(null);
  const [wind, setWind] = useState(null);
  const [loaded, setLoaded] = useState(false);

  function getWeatherData(response) {
    setTemperature(Math.round(response.data.main.temp));
    setDescription(response.data.weather[0].description);
    setIcon(response.data.weather[0].icon);
    setHumidity(response.data.main.humidity);
    setWind(Math.round(response.data.wind.speed));
    setLoaded(true);
  }

  function handleSubmit(event) {
    event.preventDefault();
    let apiKey = "477355458f09adef7ea7ed5ab3947103";
    let units = "metric";
    let apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=${units}`;
    axios.get(apiUrl).then(getWeatherData);
  }

  function showWeather(event) {
    setCity(event.target.value);
  }

  let form = (
    <header className="App-header">
      <form onSubmit={handleSubmit} id="search-form">
        <input
          type="search"
          placeholder="üåç Search City"
          className="form-control"
          id="search-enter"
          onChange={showWeather}
        />
        <button id="search-button" onClick={handleSubmit}>
          üëÄ
        </button>
        <button id="location-button">üìç</button>
        <span className="date-time" id="today">
          Mon 9. Aug ‚è∞ 12:03
        </span>
      </form>
    </header>
  );

  let weatherIcon = `http://openweathermap.org/img/wn/${icon}@2x.png`;

  if (loaded) {
    return (
      <div>
        {form}
        <ul>
          <li>{city}</li>
          <li>{description}</li>
          <li>
            <img src={weatherIcon} alt={description} />
          </li>
          <li>{temperature}¬∞C</li>
          <li>Humidity: {humidity}%</li>
          <li>Wind: {wind}km/h</li>
        </ul>
      </div>
    );
  } else {
    return form;
  }
}
